class Solution {

    private int[][] valid;
    private char[] str;

    public int isValid(int i, int j) {
        if(valid[i][j] != -1) return valid[i][j];
        if(i >= j || ((j - i + 1) & 1) == 1) return valid[i][j] = 0;
        if(i + 1 == j) return valid[i][j] = (str[i] == '(' && str[j] == ')') ? 1 : 0;
        valid[i][j] = 0;
        if(str[i]=='(' && str[j] == ')' && isValid(i + 1, j - 1) == 1) valid[i][j] = 1;
        else {
            for(int ii = i + 1; ii < j; ii += 2)
                if(isValid(i, ii) == 1 && isValid(ii + 1, j) == 1) {
                    valid[i][j] = 1;
                    break;
                }
        }
        return valid[i][j];
    }

    public int longestValidParentheses(String s) {
        if(s.length() == 0) return 0;
        str = s.toCharArray();
        valid = new int[s.length()][s.length()];
        for(var v : valid) Arrays.fill(v, -1);

        int max = 0;
        int[] cnt = new int[')' + 1];
//        for(int j = str.length - 1; j >= 0; j--) {
//            for(int i = 0; i + j < str.length; i++) {
//                if(isValid(i, j) == 1) max = Math.max(max, j - i + 1);
//            }
//        }

        for(int i = 0; i < str.length; i++) {
            cnt['('] = 0;
            cnt[')'] = 0;
            for(int j = i + 1; j < str.length; j += 2) {
                cnt[str[j]]++;
                cnt[str[j-1]]++;
                if(str[j] == '(' || cnt['('] != cnt[')']) {
                    valid[i][j] = 0;
                    continue;
                }
                if(isValid(i, j - 2) == 1) {
                    if(str[j] == ')' && str[j-1] == '(') valid[i][j] = 1;
                    else valid[i][j] = 0;
                }
                if(isValid(i, j) == 1) max = Math.max(max, j - i + 1);
            }
        }

//        System.out.println(Arrays.deepToString(valid));

        return max;
    }
}

